import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-filtro-peliculas',
  templateUrl: './filtro-peliculas.component.html',
  styleUrls: ['./filtro-peliculas.component.css']
})
export class FiltroPeliculasComponent implements OnInit {

  constructor(
    private formBuilder: FormBuilder, 
    private location: Location,
    private activatedRoute: ActivatedRoute) { }

  form: FormGroup

  generos = [
    { id: 1, nombre: 'AcciÃ³n'},
    { id: 2, nombre: 'Drama'},
    { id: 3, nombre: 'Comedia'}
  ];

  peliculas = [
    {
    titulo: "Infinity Flan",
    enCines: true,
    generos: [3],
    proximosEstrenos: true,
    poster: 'data:image/jpeg;base64,'
  },
  {
    titulo: "Frankenstein",
    enCines: false,
    generos: [1,2],
    proximosEstrenos: true,
    poster: 'data:image/jpeg;base64,'
  },
  {
    titulo: "Putos todos",
    enCines: false,
    generos: [2,3],
    proximosEstrenos: false,
    poster: 'data:image/jpeg;base64,'
  },
  {
    titulo: "Changremlins II",
    enCines: true,
    generos: [1,3],
    proximosEstrenos: false,
    poster: 'http://stampsy-eu-1.s3.amazonaws.com/uploads/09/21/2014/user/18526/617b2fe3-d3e6-43de-b703-253e14f465eb.jpg'
  },
  {
    titulo: "Revenge Of The Changuie",
    enCines: true,
    generos: [1,2,3],
    proximosEstrenos: true,
    poster: 'data:image/jpeg;base64,'
  }]

  peliculasOriginal = this.peliculas;

  formularioOriginal = {
      titulo: '',
      generoId: 0,
      proximosEstrenos: false,
      enCines: false
   };


  ngOnInit(): void {
    this.form = this.formBuilder.group(this.formularioOriginal);
    this.leerValoresURL();
    this.buscarPeliculas(this.form.value);

    this.form.valueChanges
      .subscribe(valores => {
        this.peliculas = this.peliculasOriginal;
        this.buscarPeliculas(valores);
        this.escribirParametrosBusquedaEnURL();
      })
  }

  private leerValoresURL(){
    this.activatedRoute.queryParams.subscribe((params) => {
      var objeto: any = {};

      if(params.titulo)
        objeto.titulo = params.titulo;

      if(params.generoId)
        objeto.generoId = Number(params.generoId);

      if(params.proximosEstrenos)
        objeto.proximosEstrenos = params.proximosEstrenos;
      
      if(params.enCines)
        objeto.enCines = params.enCines;
      
      this.form.patchValue(objeto);
    });

  }

  private escribirParametrosBusquedaEnURL(){
    var queryStrings = [];

    var valoresFormulario = this.form.value;

    if(valoresFormulario.titulo)
      queryStrings.push(`titulo=${valoresFormulario.titulo}`);

    if(valoresFormulario.generoId != '0')
      queryStrings.push(`generoId=${valoresFormulario.generoId}`);

    if(valoresFormulario.proximosEstrenos)
      queryStrings.push(`proximosEstrenos=${valoresFormulario.proximosEstrenos}`);

    if(valoresFormulario.enCines)
      queryStrings.push(`enCines=${valoresFormulario.enCines}`);

    this.location.replaceState('peliculas/buscar', queryStrings.join('&'));
  }

  buscarPeliculas(valores:any){
    if(valores.titulo)
      this.peliculas = this.peliculas.filter(pelicula => pelicula.titulo.indexOf(valores.titulo) !== -1);
    
    if(valores.generoId !== 0)
      this.peliculas = this.peliculas.filter(pelicula => pelicula.generos.indexOf(valores.generoId) !== -1);  

    if(valores.proximosEstrenos)
      this.peliculas = this.peliculas.filter(pelicula => pelicula.proximosEstrenos);
    
    if(valores.enCines)
      this.peliculas = this.peliculas.filter(pelicula => pelicula.enCines);
  }

  limpiar(){
    this.form.patchValue(this.formularioOriginal);
  }
}
